/*Os produtos mais vendidos*/
select produto.Nome , itens.quant 
from itens
inner join  produto  on itens.produto_CodProduto = produto.CodProduto
group by produto.Nome
order by quant DESC ;

/* Nome dos clientes que realizaram alguma compra no dia 03/07/2018*/

SELECT cliente.Nome , compra.Data 
FROM cliente 
INNER JOIN compra ON cliente.CodCliente = compra.Cliente_CodCliente
WHERE compra.Data ='2018-07-03'
order by cliente.Nome

/*Nome de clientes que consumiram acima de 100 reais*/

SELECT cliente.Nome , compra.ValorTotal 
FROM cliente 
INNER JOIN compra ON cliente.CodCliente = compra.Cliente_CodCliente
WHERE compra.ValorTotal >'100' ;

/*Nome de clientes que foram a piscina*/
SELECT cliente.Nome , itens.produto_CodProduto
FROM cliente 
INNER JOIN itens ON cliente.CodCliente = itens.Compra_Cliente_CodCliente
WHERE itens.produto_CodProduto ='24'

/*Ordem decrescente da quantia comprada com nome e email do cliente*/
SELECT cliente.Nome , cliente.email , compra.ValorTotal
FROM cliente 
INNER JOIN compra ON cliente.CodCliente = compra.Cliente_CodCliente
order by compra.ValorTotal DESC

